import React, {useState} from 'react'
import {Fullscreen,Sidebar,Circle,Imaherr,Picker} from './PizarronElements'
import Pincel from '../Images/pincel.png'


const BoardMain = () => {
  const[selectedColor, setSelectedColor] = useState("black")
  return (
  <Fullscreen>
    <Sidebar>
      <Circle><div className="color-picker-container">
      <Picker value={selectedColor} type="color" onChange={(e)=>setSelectedColor(e.target.value)} />
       </div><Imaherr src={Pincel}/></Circle>
    </Sidebar>
    </Fullscreen>
  )
}

export default BoardMain
