   let timeout;

   useEffect(()=>{
      const s = io("http://localhost:5000");
      setSocket(s)

      return ()=>{
        s.disconnect();
      }
   },[]);


    return (
    <Sketch>
        <Caanvas id="board" />
    </Sketch>
  );
};

export default Boardi;
